import styled from 'styled-components'

export const ItemContainer = styled.div`
    width: 80%;

    h3 {
        font-size: 32px;
        color: #FAFAFA
    }

    p {
        font-size 16px;
        color: #FAFAFA60;
        margin-bottom: 20px;

    }

    a.remover {
        color: #ff0000;
        margin-top: 30px;

    }

    hr {
        color: #fafafa;
        margin: 20px 0;
    }
`
